        <Route component={NotFoundPage} />
      </Switch>
      <Footer /> */}
      {/* Learning */}
      <h3>Hi Amit</h3>
      <h4>{joke}</h4>
      <button onClick={()=>newJoke()}>Get another joke</button>
    </div>
  );
}

export default App;
