import React, { Component } from 'react'
import SimpleImageSlider from "react-simple-image-slider";

class SliderImage extends Component {
    onCompleteSlide = (idx, length) => {
        console.log(`[App onCompleteSlide] ${idx}/${length}`);
        this.setState({ slideIndexText: `${idx} / ${length}`});
    }
    render() {
        const images = [
            { url: "images/MigrantOnRoad.jpg" },
            { url: "images/MigrantHealthIssue.jpg" },
            { url: "images/Migrant1.jpg" },
            { url: "images/LathiCharge.jpg" },
            { url: "images/MigrantOnCall.jpg" },
            { url: "images/GirlCarryingFather.jpeg" },
            // { url: "images/7.jpg" },
        ];
        return (
            <div className="row mt-5">
                <div className="col-12 mx-auto">
                <SimpleImageSlider
                    width="100%"
                    height={504}
                    images={images}
                    onCompleteSlide={this.onCompleteSlide}
                />
                </div>
            </div>
        )
    }
}

export default SliderImage;
