import React, { Component } from 'react'

class RefUseRef extends Component {
    myHeading = React.createRef();
    myinput = React.createRef();
    componentDidMount() {
        console.log(this.myHeading.current.innerText);
    }
    render() {
        return (
            <div>
                <h3 ref={this.myHeading}>Ref and UseRef</h3>
                <input type="text" ref={this.myinput} />
                <button onClick={()=>this.getvalue()}>get value</button>
            </div>
        )
    }
}

export default RefUseRef;
