import React from 'react'

function ContextApi() {
    const MyContext = React.createContext()

    const Coder3 = () => {
        return (
            <MyContext.Consumer>
                {
                    (data) => {
                        return (
                            <React.Fragment>
                                <h1>myname is {data.name}</h1>
                                <h1>myname is {data.name}</h1>
                            </React.Fragment>
                        )
                    }
                }
            </MyContext.Consumer>
        )
    }
    const Coder2 = () => {
        return <Coder3 />
    }
    const Coder = () => {
        return <Coder2 />
    }
    return (
        <div>
            Context Api
            <MyContext.Provider value={{ name: "Amit", age: 27 }}>
                <Coder />
            </MyContext.Provider>

        </div>
    )
}

export default ContextApi;
