import React, { Component } from 'react'
import SimpleImageSlider from "react-simple-image-slider";

class SliderImage extends Component {
    constructor() {
        super();
        this.state = {
            useGPURender: true,
            showNavs: true,
            showBullets: true,
            navStyle: 1,
            slideDuration: 0.5,
            bgColor: "#000000",
            slideIndexText: "",
        };
    }
    render() {
        const toggleOptions = ["useGPURender", "showNavs", "showBullets"];
        const images = [
            { url: "images/MigrantOnRoad.jpg" },
            { url: "images/MigrantHealthIssue.jpg" },
            { url: "images/Migrant1.jpg" },
            { url: "images/LathiCharge.jpg" },
            { url: "images/MigrantOnCall.jpg" },
            { url: "images/GirlCarryingFather.jpeg" },
            // { url: "images/7.jpg" },
        ];
        return (
            <div className="row mt-5">
                <div className="col-12 mx-auto">
                <SimpleImageSlider
                    width="100%"
                    height={504}
                    images={images}
                    showBullets={this.state.showBullets}
                    showNavs={this.state.showNavs}
                    useGPURender={this.state.useGPURender}
                    navStyle={this.state.navStyle}
                    slideDuration={this.state.slideDuration}
                    onClickNav={this.onClickNav}
                    onClickBullets={this.onClickBullets}
                    onStartSlide={this.onStartSlide}
                    onCompleteSlide={this.onCompleteSlide}
                />
                </div>
            </div>
        )
    }
}

export default SliderImage;
